import React, {useState} from 'react';

import ExpenseForm from './ExpenseForm';
import './NewExpense.css';

const NewExpense = (props) => {
  let orignalReturn = <div className='new-expense'>
                        <button onClick={changeElementHandler}>Add New Expense</button>
                      </div>;

  const [newReturn, setNewReturn] = useState(orignalReturn);

  const changeElementHandler = () => {
    setNewReturn(<div className='new-expense'>
                  <ExpenseForm onSaveExpenseData={saveDataHandler}/>
                </div>);
  }

  const saveDataHandler = (expenseData) => {
    props.onLiftNewExpenseData(expenseData);
  }

  return (
    <div className='new-expense'>
      <ExpenseForm onSaveExpenseData={saveDataHandler}/>
    </div>
  );
};

export default NewExpense;
