import React, {useState} from 'react';

import './ExpenseForm.css';

const ExpenseForm = (props) => {
  /* const [enteredTitle, setEnteredTitle] = useState(''); */

  let enteredTitle = '';
  let enteredAmount = '';
  let enteredDate = '';

  function enteredTitleHandler(event) {
    enteredTitle = event.target.value;
  }
  function enteredAmountHandler(event) {
    enteredAmount = event.target.value;
  }
  function enteredDateHandler(event) {
    enteredDate = event.target.value;
  }
  function submitHandler(event) {
    event.preventDefault();
    let expenseData = {
      title: enteredTitle,
      amount: enteredAmount,
      date: new Date(enteredDate),
    }
    props.onSaveExpenseData(expenseData)
  }

  return (
    <form onSubmit={submitHandler}>
      <div className='new-expense__controls'>
        <div className='new-expense__control'>
          <label>Title</label>
          <input type='text' onChange={enteredTitleHandler}/>
        </div>
        <div className='new-expense__control'>
          <label>Amount</label>
          <input type='number' min='0.01' step='0.01' onChange={enteredAmountHandler}/>
        </div>
        <div className='new-expense__control'>
          <label>Date</label>
          <input type='date' min='2019-01-01' max='2022-12-31' onChange={enteredDateHandler}/>
        </div>
      </div>
      <div className='new-expense__actions'>
        <button type='submit'>Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
